#!/bin/bash# Radio Browser Extension - Clear Recently Played Script# SPDX-License-Identifier: GPL-3.0-or-later# Copyright 2014 The moOde audio player project / Tim Curtis# Extension base pathEXT_PATH="/var/www/extensions/installed/radio-browser"RECENT_FILE="$EXT_PATH/cache/recently_played.json"echo "Radio Browser: Clearing recently played..."if [ -f "$RECENT_FILE" ]; then    # Backup first    cp "$RECENT_FILE" "$RECENT_FILE.bak" 2>/dev/null        # Clear the file    echo "[]" > "$RECENT_FILE"        # Fix permissions    chown www-data:www-data "$RECENT_FILE"    chmod 664 "$RECENT_FILE"        echo "Radio Browser: Recently played cleared!"    echo "  - Backup saved to: $RECENT_FILE.bak"else    echo "Radio Browser: No recently played file found."fi