#!/bin/bash# Radio Browser Extension - Flush Cache Script# SPDX-License-Identifier: GPL-3.0-or-later# Copyright 2014 The moOde audio player project / Tim Curtis# Extension base pathEXT_PATH="/var/www/extensions/installed/radio-browser"CACHE_PATH="$EXT_PATH/cache"echo "Radio Browser: Flushing cache..."# Count files beforeCACHE_COUNT=$(find "$CACHE_PATH" -maxdepth 1 -name "*.json" -type f 2>/dev/null | wc -l)IMAGE_COUNT=$(find "$CACHE_PATH/images" -type f 2>/dev/null | wc -l)# Remove cached API responses (but not recently_played.json)find "$CACHE_PATH" -maxdepth 1 -name "*.json" ! -name "recently_played.json" -type f -delete 2>/dev/null# Remove cached imagesrm -rf "$CACHE_PATH/images/"* 2>/dev/null# Clear log file (but keep it)if [ -f "$CACHE_PATH/radio-browser.log" ]; then    echo "[$(date -Iseconds)] Cache flushed" > "$CACHE_PATH/radio-browser.log"fiecho "Radio Browser: Cache flushed successfully!"echo "  - Removed $CACHE_COUNT cached API responses"echo "  - Removed $IMAGE_COUNT cached images"echo "  - Log file cleared"